/*!

=========================================================
* Paper Dashboard React - v1.3.0
=========================================================

* Product Page: https://www.creative-tim.com/product/paper-dashboard-react
* Copyright 2021 Creative Tim (https://www.creative-tim.com)

* Licensed under MIT (https://github.com/creativetimofficial/paper-dashboard-react/blob/main/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { useEffect, useState } from "react";
import DynamicTable from "components/Tables/table";
// react plugin used to create charts
import { Line, Pie } from "react-chartjs-2";
// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  Row,
  Col,
  Table
} from "reactstrap";
// core components
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';



const default_stock_tickers = "AAPL, TSLA";

const table = <Table>
<thead>
  <tr>
    <th>Symbol</th>
    <th>Name</th>
    <th>Price</th>
    <th>Change</th>
  </tr>
</thead>
<tbody>
  <tr>
    <th scope="row">TSLA</th>
    <td>Tesla</td>
    <td>23</td>
    <td>2</td>
  </tr>
  <tr>
    <th scope="row">MSFT</th>
    <td>Microsft</td>
    <td>321</td>
    <td>2</td>
  </tr>
  <tr>
    <th scope="row">AAPL</th>
    <td>Apple</td>
    <td>34</td>
    <td>3</td>
  </tr>

</tbody>
</Table>;

function Home() {

  const [top_gainers, setTopGainers] = useState({});
  const [top_losers, setTopLosers] = useState({});


  const time_range = 'daily';
  async function get_top_gainers(){
    fetch(`http://localhost:8888/home/get_top_gainers_api/${time_range}`)
    .then((response) => response.json())
    .then((responseData) => {
      console.log(responseData["result"])
      return responseData["result"]
    }).catch(error => console.warn(error))
  }

  async function get_top_losers(){
    fetch(`http://localhost:8888/home/get_top_losers_api/${time_range}`)
    .then((response) => response.json())
    .then((responseData) => {
      console.log(responseData["result"])
      return responseData["result"]
    }).catch(error => console.warn(error))
  }

  function TopGainersOrLosers(type, height, width) {
    let result;
    if (type === 'gainers'){
      result = get_top_gainers()
    }
    else{
      result = get_top_losers()
    }

    return (
      <div style={{
        maxHeight: `${height}px`,
        maxWidth: `${width}px`,
        overflowY: 'auto',
        overflowX: 'auto'
      }}>
        <DynamicTable bordered height={height} data={result}></DynamicTable>
      </div>
    )
  } 


  return (
    <>
      <div className="content">
        <Row>
          <Col md="6">
            <Card>
              <CardHeader>
                <CardTitle tag="h5">Top Gainers</CardTitle>
              </CardHeader>
              <CardBody>
                {TopGainersOrLosers("gainers", 400, 200)}
              </CardBody>
            </Card>
          </Col>
          <Col md="6">
            <Card>
              <CardHeader>
                <CardTitle tag="h5">Top Losers</CardTitle>
              </CardHeader>
              <CardBody>
              {TopGainersOrLosers("losers", 400, 200)}
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col>
            <Card>
            <CardTitle tag="h5">Market News</CardTitle>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default Home;
