import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { Nav } from "reactstrap";
// javascript plugin used to create scrollbars on windows
import PerfectScrollbar from "perfect-scrollbar";
import classnames from 'classnames';

import {
  Row, Col,
  Button,
  CardHeader,
  CardTitle,
  Card,
  CardBody,
  Progress,
  TabContent,
  TabPane,
} from 'reactstrap';
import Form from 'react-bootstrap/Form';
import Stock_info_form from "components/Forms/FetchStockData.js"


export function SwitchTab({func1, func2, title}) {
  const [active_state, setActive_state] = useState('0')

  return (
    <>
        <CardHeader className="card-header-tab">
          <div className="card-header-title">
            <CardTitle tag="h5">{title}</CardTitle>
          </div>
          <div className="btn-actions-pane-right">
              <Button outline
                      className={"border-0 btn-pill btn-wide btn-transition " + classnames({active: active_state === '0'})}
                      color="primary" onClick={() => {
                  setActive_state('0');
              }}>Get Hist Data</Button>
              <Button outline
                      className={"ml-1 btn-pill btn-wide border-0 btn-transition " + classnames({active: active_state === '1'})}
                      color="primary" onClick={() => {
                  setActive_state('1')
              }}>Upload CSV</Button>
          </div>
        </CardHeader>
        <TabContent activeTab={active_state}>
          <TabPane tabId="0">
              <CardBody className="pt-2">
                <div class="flexbox-container">
                  <Stock_info_form fetch_data={func1}/>
                </div>
              </CardBody>
          </TabPane>
        </TabContent>
        <TabContent activeTab={active_state}>
          <TabPane tabId="1">
              <CardBody className="pt-2">
                <div><input type="file" onChange={func2} /></div>                  
              </CardBody>
          </TabPane>
        </TabContent>
    </>
  );
}
