import React, { useEffect, useState } from "react";
import Stock_info_form from "components/Forms/FetchStockData.js";
import {StockPriceChart} from "variables/charts.js" ;

// reactstrap components
import { Card, CardHeader, CardBody, CardTitle, Row, Col } from "reactstrap";

function Analysis() {

  const [stock_data, setStock_data] = useState({});
  const [file, upLoadFile] = useState();

  async function fetch_data (tickers, start_date, end_date) {

    let request_url = `http://localhost:8000/data/load_hist_stock_price/${start_date}/${end_date}?`;
    const ticker_list = tickers.split(',').map(function(item){
      return item.trim();
    })
    for (let i = 0; i < ticker_list.length; i++){
      if (i == 0){
        request_url += `q=${ticker_list[i]}`;
        console.log(`q=${ticker_list[i]}`)
      }
      else{
        request_url += `&q=${ticker_list[i]}`;
        console.log(`q=${ticker_list[i]}`)
      }

    };
    const response = await fetch(request_url);
    const data = await response.json();
    const dataset = [];
    for (let i = 0; i < ticker_list.length; i ++){
      let data_dict = {
        label:ticker_list[i],
        pointRadius: 0,
        pointHoverRadius: 0,
        borderWidth: 3,
        tension: 0.4,
        fill: false,
        data: data[ticker_list[i]]};
        dataset.push(data_dict)
    };
      const line_chart_data = {labels: data['date'], datasets: dataset};
      setStock_data(line_chart_data)
  }
  return (
    <>
      <div className="content">
      <Row>
          <Card>
            <CardHeader>
                  <CardTitle tag="h5">Select Stock Data</CardTitle>
            </CardHeader>

            <Col md='2'>
              <CardBody>
                <Stock_info_form fetch_data={fetch_data} />
              </CardBody>
            </Col>
            <Col md="10">
              <CardBody>
                <StockPriceChart stock_data={stock_data} />
              </CardBody>
            </Col>
          </Card>
      </Row>
        <Row>
          <Col md="12">
            <Card className="analysis">
              <CardHeader>
                <CardTitle tag="h5">PCA</CardTitle>
                <p className="card-category">
                </p>
              </CardHeader>
              <CardBody className="PCA">
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default Analysis;
