import React, { useEffect, useState } from "react";
import Stock_info_form from "components/Forms/FetchStockData.js";
import {StockPriceChart} from "variables/charts.js" ;
import { SwitchTab } from "components/Switch/ToggleSwitch";
import Csv_reader from "utils/data.js"

// reactstrap components
import { Card, CardHeader, CardBody, CardTitle, Row, Col } from "reactstrap";

function Analysis() {

  const [stock_data, setStock_data] = useState({});
  const [data, SetAnalyticsData] = useState({});
  //const [file, upLoadFile] = useState();

  // function handleFileUpload(event) {
  //   upLoadFile(event.target.files[0])
  // }

  function line_chart_formatter(data){
    const dataset = [];
    for (let i = 0; i < Object.keys(data).length; i ++){
      let data_dict = {
        label:Object.keys(data)[i],
        pointRadius: 0,
        pointHoverRadius: 0,
        borderWidth: 3,
        tension: 0.4,
        fill: false,
        data: data[Object.keys(data)[i]]};
      dataset.push(data_dict)
    };
    return dataset;
  }


  const handleOnDrop = (data) => {
    let parsed_data = {};
    for (let i = 0; i < data.length; i++){
      if (i ==0){
        for (let j = 0; j < data[0]["data"].length; j++){
          parsed_data[data[i]["data"][j]]=[];
        }
      }
      else{
        for (let j = 0; j < data[i]["data"].length; j++){
          parsed_data[data[0]["data"][j]].push(data[i]["data"][j]);
        }
      }
    }
    SetAnalyticsData(parsed_data);
  }


  async function fetch_data (tickers, start_date, end_date) {

    let request_url = `http://localhost:8000/data/load_hist_stock_price/${start_date}/${end_date}?`;
    const ticker_list = tickers.split(',').map(function(item){
      return item.trim();
    })
    for (let i = 0; i < ticker_list.length; i++){
      if (i == 0){
        request_url += `q=${ticker_list[i]}`;
        console.log(`q=${ticker_list[i]}`)
      }
      else{
        request_url += `&q=${ticker_list[i]}`;
        console.log(`q=${ticker_list[i]}`)
      }

    };
    const response = await fetch(request_url);
    const data = await response.json();

    const line_chart_data = {labels: data['date'], datasets: line_chart_formatter(data)};
    setStock_data(line_chart_data);
    SetAnalyticsData(data);
  }
  const tickers = Object.keys(data).filter(item => item !== 'date').join(", ");
  //TODO: categorize analysis: option -> options, filter -> filters
  return (
    <>
      <div className="content">
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
                  <CardTitle tag="h5">Get Data</CardTitle>
            </CardHeader>
            <CardBody>
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col md="12">
          <Card>
            <CardHeader>
                  <CardTitle tag="h5">Get Data</CardTitle>
            </CardHeader>
            <CardBody>
              <Csv_reader handleOnDrop={handleOnDrop}/>
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col md="12">
            <Card>
              <CardBody>
                <StockPriceChart stock_data={stock_data} />
              </CardBody>
            </Card>
        </Col>
      </Row>

      <Row>
        <Col md="12">
          <Card className="analysis">
            <CardHeader>
              <CardTitle tag="h5">Stock Filters</CardTitle>
              <p className="card-category">
                Tickers: {tickers}
              </p>
            </CardHeader>
            <CardBody className="PCA">
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col md="12">
          <Card className="analysis">
            <CardHeader>
              <CardTitle tag="h5">PCA</CardTitle>
              <p className="card-category">
                Tickers: {tickers}
              </p>
            </CardHeader>
            <CardBody className="PCA">
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col md="12">
          <Card className="analysis">
            <CardHeader>
              <CardTitle tag="h5">Liquidity</CardTitle>
              <p className="card-category">
                Tickers: {tickers}
              </p>
            </CardHeader>
            <CardBody className="PCA">
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col md="12">
          <Card className="analysis">
            <CardHeader>
              <CardTitle tag="h5">Binomial</CardTitle>
              <p className="card-category">
                Tickers: {tickers}
              </p>
            </CardHeader>
            <CardBody className="PCA">
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col md="12">
          <Card className="analysis">
            <CardHeader>
              <CardTitle tag="h5">Black Sholes</CardTitle>
              <p className="card-category">
                Tickers: {tickers}
              </p>
            </CardHeader>
            <CardBody className="PCA">
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col md="12">
          <Card className="analysis">
            <CardHeader>
              <CardTitle tag="h5">Geske</CardTitle>
              <p className="card-category">
                Tickers: {tickers}
              </p>
            </CardHeader>
            <CardBody className="PCA">
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
