import React, { useEffect, useState } from "react";
import Stock_info_form from "components/Forms/FetchStockData.js";
import {StockPriceChart} from "variables/charts.js" ;
import { SwitchTab } from "components/Switch/ToggleSwitch";
import Csv_reader from "utils/data.js"
import DynamicTable from "components/Tables/table.js"
import {ButtonDropdown, DropdownItem, DropdownMenu, DropdownToggle, Form, FormGroup, Label, Input, Button} from "reactstrap";

// reactstrap components
import { Card, CardHeader, CardBody, CardTitle, Row, Col } from "reactstrap";

function PortfolioAnalysis() {

  const [stock_data, setLineData] = useState({});
  const [data, setAnalyticsData] = useState({});
  const [file_info, setFileInfo] = useState("No File Uploaded");
  const [weights, setWeights] = useState();
  const [basic_info, setBasicInfo] = useState({});
  const [var_dropdown_state, setVaRDropdownState] = useState(false);
  const [var_dropdown_val, setVaRDropdownValue] = useState("Historical VaR");
  //const [file, upLoadFile] = useState();

  // function handleFileUpload(event) {
  //   upLoadFile(event.target.files[0])
  // }

  function chart_data_parser(data){
    const dataset = [];
    let labels;
    if ("date" in data){
      labels = data["date"];
      delete data["date"];
    }
    else if ("Date" in data){
      labels = data["Date"];
      delete data["Date"];
    }
    else{
      labels = [...Array(Object.keys(data).length).keys()];
    }
    for (let i = 0; i < Object.keys(data).length; i ++){
      let data_dict = {
        label:Object.keys(data)[i],
        pointRadius: 0,
        pointHoverRadius: 0,
        borderWidth: 3,
        tension: 0.4,
        fill: false,
        data: data[Object.keys(data)[i]]};
      dataset.push(data_dict)
    };

    const line_chart_data = {labels: labels, datasets: dataset};
    return line_chart_data;
  }



  function handleOnComplete (data) {
    let parsed_data = {};
    for (let i = 0; i < data["data"].length; i++){
      if (i ==0){
        for (let j = 0; j < data["data"][0].length; j++){
          parsed_data[data["data"][0][j]]=[];
        }
      }
      else{
        for (let j = 0; j < data["data"][i].length; j++){
          parsed_data[data["data"][0][j]].push(data["data"][i][j]);
        }
      }
    }
    setBasicInfo({});
    setLineData(chart_data_parser(parsed_data));
    setAnalyticsData(parsed_data);    
  }


  function handleOnRemove () {
    setLineData({});
    setAnalyticsData({});
    setBasicInfo({});

  }


  function EnterWeights (){
    function onChange (e) {
      setWeights(e.currentTarget.textContent)
    }

    function onSubmit (e) {
      const weights_array = weights.split(",").map(Number);
      get_basic_info(data, weights_array);
    }
    return (
      <Form inline onSubmit={onSubmit}>
        <FormGroup>
          <Label className="mr-sm-2">Enter weights</Label>
          <Input placeholder="e.g. 0.2, 0.2, 0.6" onChange={onChange}/>
        </FormGroup>
        <Button>Enter</Button>
      </Form>
    )
  }



  function BasicInfoTable() {
    console.log(basic_info);
    if ((basic_info != null) && (Object.keys(basic_info).length > 0)){
      return (
        <>
        <DynamicTable data={basic_info} />
        </>
      )
    }
    else {
      return (<></>)
    }
  }


  function DropdownVaR() {
    function toggle (e){
      setVaRDropdownState(!var_dropdown_state);
    }

    function onclick(e){
      setVaRDropdownValue(e.currentTarget.textContent)
    }

    const items = ["Historical VaR", "Parametric VaR", "Monte Carlo VaR"];
    return (
      <>
        <ButtonDropdown isOpen={var_dropdown_state} toggle={toggle}>
          <DropdownToggle caret>
            {var_dropdown_val}
          </DropdownToggle>
          <DropdownMenu>
            {items.map(e => {
              return <DropdownItem onClick={onclick}>{e}</DropdownItem>
            })}
          </DropdownMenu>
        </ButtonDropdown>
      </>
    )
  }


  function VaRForm() {

    function onClick(e){
      setVaRDropdownValue(e.currentTarget.textContent)
    }

    function onChange(e){

    }

    return (
      <Form inline>
        <FormGroup>
          <Label>Select Calculation Method</Label>
          <Input type="select" name="select" onChange={onClick}>
            <option>Historical VaR</option>
            <option>Parametric VaR</option>
            <option>Monte Carlo VaR</option>
          </Input>
        </FormGroup>
        <FormGroup>
          <Label>
            Enter level
          </Label>
          <Input type="number" onChange={onChange}>
          </Input>
        </FormGroup>
      </Form>
    )
  }



  async function fetch_data (tickers, start_date, end_date) {

    let request_url = `http://localhost:8888/data/load_hist_stock_price/${start_date}/${end_date}?`;
    const ticker_list = tickers.split(',').map(function(item){
      return item.trim();
    })
    for (let i = 0; i < ticker_list.length; i++){
      if (i == 0){
        request_url += `q=${ticker_list[i]}`;
      }
      else{
        request_url += `&q=${ticker_list[i]}`;
      }

    };
    const response = await fetch(request_url);
    const data = await response.json();
    setBasicInfo({})
    setLineData(chart_data_parser(data));
    setAnalyticsData(data);
  }

  async function get_basic_info(data, weights) {
    const response = await fetch('http://localhost:8888/portfolio_analysis/get_basic_info', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        data: data,
        weights: weights
      })
    })

    const result = await response.json();
    const parsed = JSON.parse(result["result"])
    setBasicInfo(parsed);
    return parsed
  }

  async function get_var_info(data, method, weights){
    const response = await fetch('http://localhost:8888/portfolio_analysis/valueatrisk', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        weights: weights,
        method: method,
        data: data
      })
    })

    const result = await response.json();
    const parsed = JSON.parse(result["result"])
    return parsed
  }

  async function sharpe_ratio(data, rf){
    const response = await fetch('http://localhost:8888/portfolio_analysis/sharpe_ratio', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        rf: rf,
        data: data
      })
    })

    const result = await response.json();
    const parsed = JSON.parse(result["result"])
    return parsed
  }





  const tickers = Object.keys(data).filter(item => item !== 'date').join(", ");

  const stock_info_form = <Stock_info_form fetch_data={fetch_data}/>;
  const file_upload = <Csv_reader handleOnComplete={handleOnComplete} handleOnRemove={handleOnRemove}/>;
  //TODO: categorize analysis: option -> options, filter -> filters
  return (
    <>
      <div className="content">
      <Row>
        <Col md="12">
          <Card>  
            <CardHeader>
                  <CardTitle tag="h5">Get Data</CardTitle>
            </CardHeader>
            <CardBody>
              <SwitchTab Component1 = {stock_info_form} Component2 = {file_upload} button_name1 = "Fetch Data" button_name2 = "Upload File"/>
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col md="12">
            <Card>
              <CardBody>
                <StockPriceChart stock_data={stock_data} />
              </CardBody>
            </Card>
        </Col>
      </Row>
      <Row>
        <Col>
          <Card>
            <CardBody>{EnterWeights()}</CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col md="12">
          <Card className="analysis">
            <CardHeader>
              <CardTitle tag="h5">Basic statistics</CardTitle>
              <p className="card-category">
                Tickers: {tickers}
              </p>
            </CardHeader>
            <CardBody className="basic_info">
            {BasicInfoTable()}
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col md="12">
          <Card className="analysis">
            <CardHeader>
              <CardTitle tag="h5">PCA Analysis</CardTitle>
              <p className="card-category">
                Tickers: {tickers}
              </p>
            </CardHeader>
            <CardBody className="PCA">
            </CardBody>
          </Card>
        </Col>
      </Row>
      <Row>
        <Col md="12">
          <Card className="analysis">
            <CardHeader>
              <CardTitle tag="h5">Optimization</CardTitle>
              <p className="card-category">
                Tickers: {tickers}
              </p>
            </CardHeader>
            <CardBody className="PCA">
            </CardBody>
          </Card>
        </Col>
      </Row>

      <Row>
        <Col md="12">
          <Card className="analysis">
            <CardHeader>
              <CardTitle tag="h5">VaR Analysis</CardTitle>
              <p className="card-category">
                Tickers: {tickers}
              </p>
            </CardHeader>
            <CardBody className="PCA">
              Select method of Calculation  {DropdownVaR()}


            </CardBody>
          </Card>
        </Col>
      </Row>
      </div>
    </>
  );
}

export default PortfolioAnalysis;
