import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { Nav } from "reactstrap";
// javascript plugin used to create scrollbars on windows
import PerfectScrollbar from "perfect-scrollbar";
import classnames from 'classnames';

import {
  Row, Col,
  Button,
  CardHeader,
  CardTitle,
  Card,
  CardBody,
  Progress,
  TabContent,
  TabPane,
} from 'reactstrap';

export function SwitchTab({handleFileUpload, title}) {
  const [active_state, setActive_state] = useState('0')

  return (
    <>
      <Card>
        <CardHeader className="card-header-tab">
          <div className="card-header-title">
            <CardTitle tag="h5">{title}</CardTitle>
          </div>
          <div className="btn-actions-pane-right">
              <Button outline
                      className={"border-0 btn-pill btn-wide btn-transition " + classnames({active: active_state === '0'})}
                      color="primary" onClick={() => {
                  setActive_state('0');
              }}>Get Hist Data</Button>
              <Button outline
                      className={"ml-1 btn-pill btn-wide border-0 btn-transition " + classnames({active: active_state === '1'})}
                      color="primary" onClick={() => {
                  setActive_state('1')
              }}>Upload CSV</Button>
          </div>
        </CardHeader>
        <TabContent activeTab={active_state}>
          <TabPane tabId="0">
              <CardBody className="pt-2">
                <div>Get Hist Data</div>
              </CardBody>
          </TabPane>
        </TabContent>
        <TabContent activeTab={active_state}>
          <TabPane tabId="1">
              <CardBody className="pt-2">
                <div><input type="file" onChange={handleFileUpload} /></div>                  
              </CardBody>
          </TabPane>
        </TabContent>
      </Card>
    </>
  );
}
